class Solution:
    def kthSmallestPath(self, destination: List[int], k: int) -> str:
        mem = {}
        def get_mx_cnt(h_cnt, v_cnt):
            if h_cnt == 0 or v_cnt == 0:
                return 1
            if (h_cnt, v_cnt) in mem:
                return mem[(h_cnt, v_cnt)]

            mem[(h_cnt, v_cnt)] = get_mx_cnt(h_cnt - 1, v_cnt) + get_mx_cnt(h_cnt, v_cnt - 1)
            return mem[(h_cnt, v_cnt)]

        def get_kth(h_cnt, v_cnt, k):
            if k == 1 or v_cnt == 0 or h_cnt == 0:
                return ["H"] * h_cnt + ["V"] * v_cnt, 1

            mx_htop_cnt = get_mx_cnt(h_cnt - 1, v_cnt)
            if mx_htop_cnt >= k:
                # H top
                sub_res, nh_cnt = get_kth(h_cnt - 1, v_cnt, k) 
                return ["H"] + sub_res, nh_cnt
            else:
                # V top
                sub_res, nv_cnt = get_kth(h_cnt, v_cnt - 1, k - mx_htop_cnt)
                return ["V"] + sub_res, nv_cnt + mx_htop_cnt

        res, _ = get_kth(destination[1], destination[0], k)
        return "".join(res)
