import java.math.BigInteger;

class Solution {
    public String kthSmallestPath(int[] destination, int k) {
        return find("", destination[0], destination[1], k);
    }

    private String find(String current, int v, int h, long k) {
        if (v == 0 && h == 0) return current;
        BigInteger totalCombs = combinations(v+h, h);
        BigInteger hCombs = combinations(v+h-1, h-1);
        if (v > 0 && (BigInteger.valueOf(k).compareTo(hCombs) > 0 || h == 0)) {
            return find(current + 'V', v-1, h, k - hCombs.longValue());
        }
        else {
            return find(current + 'H', v, h-1, k);
        }
    }

    private BigInteger factorial(int n) {
        BigInteger result = BigInteger.ONE;
        for (int i = 2; i <= n; i++)
            result = result.multiply(BigInteger.valueOf(i));
        return result;
    }

    private BigInteger combinations(int total, int rows) {
        return (factorial(total)).divide(((factorial(rows)).multiply((factorial(total-rows)))));
    }

}
