        int col = 0;
        StringBuilder builder = new StringBuilder();
        while (row < desRow && col < desCol) {

            if (grid[row][col + 1] >= k) {
                builder.append("H");
                col++;
            } else {
                k -= grid[row][col + 1];
                builder.append("V");
                row++;
            }
