func factorialRange(n, end uint64) uint64 {
	if n < end {
		return 1
	}
	return n * factorialRange(n-1, end)
}

func nCr(n, r uint64) int {
	// To avoid overflow
	return int(factorialRange(n, n-r+1) / factorialRange(r, 2))
}

func duplicateChar(c string, n int) string {
	str := ""
	for i := 0; i < n; i++ {
		str += c
	}

	return str
}

func kthSmallestPath(destination []int, k int) string {
	v, h := destination[0], destination[1]
	if v == 0 {
		return duplicateChar("H", h)
	}
	if h == 0 {
		return duplicateChar("V", v)
	}

	for i := h; ; i-- {
		combinations := nCr(uint64(h+v-i-1), uint64(v-1))
		if k-combinations <= 0 || i == 0 {
			return duplicateChar("H", i) + "V" + kthSmallestPath([]int{v - 1, h - i}, k)
		}

		k -= combinations
	}
}
