class Solution {
    static int dp[31][31];
public:
    // choose j items from i items
    int get(int i, int j) {
        if(i < j) return 0;
        if(i == j) return 1;
        if(j == 0) return 1;
        if(j == 1) return i;
        if(dp[i][j]) return dp[i][j];
        return dp[i][j] = get(i - 1, j) + get(i - 1, j - 1);
    }

    string kthSmallestPath(vector<int>& dest, int k) {
        int x = dest[1], y = dest[0];
        string res;
        for(int i = x + y; i > 0; i--)
        {
            if(get(i-1, y) >= k) {
                res.push_back('H');
                x--;
            }
            else{
                res.push_back('V');
                k -= get(i-1, y);
                y--;
            }
        }
        return res;
    }
};
