class Solution {
public:
    string kthSmallestPath(vector<int>& destination, int k) {
        c[0][0] = 1;
        for (int i = 1; i <= 31; i++) {
            c[i][0] = 1;
            for (int j = 1; j <= 31; j++) {
                c[i][j] = c[i - 1][j - 1] + c[i - 1][j];
            }
        }
        int v = destination[0], h = destination[1];
        return solve(h, v, k);
    }
private:
    int c[32][32];
    string solve(int h, int v, int k) {
        int p = v;
        int sum = 0;
        while (true) {
            if (sum + c[p - 1][v - 1] < k) {
                sum += c[p - 1][v - 1];
                p++;
            } else if (sum + c[p - 1][v - 1] == k) {
                return string(h + v - p, 'H') + string(v, 'V') + string(p - v, 'H');
            } else {
                return string(h + v - p, 'H') + string(1, 'V') + solve(p - v, v - 1, k - sum);
            }
        }
    }
};
