public String kthSmallestPath(int[] dest, int k) {
	final int MX = 33;
	int comb[][] = new int[MX][MX];
	for (int n = 1; n < MX; n++) {
		comb[n][1] = n;
		comb[n][n] = 1;
	}
	for (int n = 2; n < MX; n++)
		for (int r = 2; r < n; r++)
			comb[n][r] = comb[n - 1][r] + comb[n - 1][r - 1];

	int t = dest[0] + dest[1];
	int rem = dest[0];

	StringBuilder builder = new StringBuilder();
	for (int i = 0; i < t; i++) {
		if (comb[t - i - 1][rem] < k && rem > 0) {
			builder.append("V");
			k -= comb[t - i - 1][rem];
			rem--;
		} else
			builder.append("H");
	}
	return builder.toString();
}
