class Solution {
    public String kthSmallestPath(int[] destination, int k) {
        int c = destination[1];
        int r = destination[0];

        StringBuilder res = new StringBuilder();
        while (r > 0 || c > 0) {
            if (r == 0) {
                res.append('H');
                c--;
                continue;
            }
            if (c == 0) {
                res.append('V');
                r--;
                continue;
            }
            double total = nCr(r, c - 1);
            if (k > total) {
                k -= total;
                res.append('V');
                r--;
            } else {
                res.append('H');
                c--;
            }
        }
        return res.toString();
    }

    double nCr(int m, int n) {
        if (m > n) return nCr(n, m);
        double res = 1;

        for (int i = 0; i < n; i++) {
            res = res * (m + n - i);
        }
        for (int i = 1; i <= n; i++) {
            res /= i;
        }

        return res;
    }
}
