long long nCr(vector<long long>& factorial, int n, int k) {
    if (n <= 20) return factorial[n] / (factorial[k] * factorial[n - k]);
    else {
        long long numerator = 1;
        for (int i = max(k, n - k) + 1; i <= n ; i++) numerator *= i;
        return numerator / factorial[min(k, n - k)];
    }
}
string kthSmallestPath(vector<int>& destination, int k) {
    int v = destination[0], h = destination[1], cur = k;
    vector<long long> factorial(min(20, h + v + 1) + 1, 1);  // factorial[n] = n!
    string res = "";
    for (int i = 1; i <= min(20, h + v + 1); i++) factorial[i] = i * factorial[i - 1];
    while (h > 0 || v > 0) {
        if (!v || !h) {
            res += string(h, 'H') + string(v, 'V');
            break;
        }
        if (cur > nCr(factorial, h + v - 1, h - 1)) {
            cur -= nCr(factorial, h + v - 1, h - 1);
            res += 'V';
            v--;
        }
        else {
            res += 'H';
            h--;
        }
    }
    return res;
}

