const int mod = 1e9+7;

class Solution {
public:
    int countPaths(int n, vector<vector<int>>& roads) {


        vector<pi> adj[n];    // node-> pair< adjNode, distance>
        for(auto &it : roads)
        {
            adj[it[0]].push_back({it[1],it[2]});
            adj[it[1]].push_back({it[0],it[2]});
        }

        priority_queue<pi, vector<pi>, greater<pi>> pq;
        vector<ll> d(n,LONG_MAX), cnt(n,0);
        pq.push({0,0});
        d[0] = 0;
        cnt[0] = 1;

        while(!pq.empty())
        {
            ll node = pq.top().second;
            ll dist = pq.top().first;
            pq.pop();

            for(auto &it : adj[node])
            {
                ll nextNode = it.first;
                ll nextDist = it.second;

                if(dist + nextDist < d[nextNode])
                {
                    cnt[nextNode] = cnt[node]; //just inherit the no. of shortest path found till node
                    d[nextNode] = dist + nextDist;
                    pq.push({d[nextNode],nextNode});
                }

                else if(dist + nextDist == d[nextNode])
                {
                    cnt[nextNode] = (cnt[nextNode] + cnt[node])%mod;  //(the no. of shortest path found till node + the no. of shortest path found till nextNode)
                }

                else continue;
            }
        }

        return cnt[n-1]%mod;
    }
};
