        vector<long long> ways(n, 0);
        vector<long long> dist(n, LONG_MAX);
        priority_queue<pair<long long, long long>, vector<pair<long long, long long>>, greater<pair<long long, long long>>> pq; // dist node
        pq.push({0, 0});
