class Solution {
public:
    struct cmp {
        bool operator()(const vector<long long> &left, const vector<long long> &right){
          return left[0] >= right[0];
        }
    };

    vector<long long> dijkstra(int n, vector<vector<vector<long long>>> &adj) {
        vector<long long> distances(n, LONG_MAX);
        priority_queue<vector<long long>, vector<vector<long long>>, cmp> Q;
        distances[n-1] = 0;      
        Q.push({distances[n-1], n-1});      
        while(!Q.empty()){
            vector<long long> vec = Q.top(); Q.pop();
            long long distanceSoFar = vec[0];						
            int node = vec[1];        
			if(distanceSoFar > distances[node]) continue;
            for(vector<long long> vecNeigh: adj[node]){
                int node_neigh = vecNeigh[0];
                long long distanceBetweenTwo = vecNeigh[1];          
                if(distanceSoFar + distanceBetweenTwo < distances[node_neigh]){
                    distances[node_neigh] = distanceSoFar + distanceBetweenTwo;
                    Q.push({distances[node_neigh], node_neigh});                         
                }
            }                    
        }

        return distances;            
    }

    int countWays(int u, vector<vector<vector<long long>>> &adj, vector<long long> &distances, long long toCoverMore, vector<long long> &dp){
        int n = adj.size();  

        if(dp[u]!=-1) return dp[u];

        if(u==n-1){            
            if(toCoverMore == 0) return 1;
            return 0;
        }

        long long res = 0;

        for(vector<long long> vecNeigh: adj[u]){
            int node_neigh = vecNeigh[0];
            long long distanceBetweenTwo = vecNeigh[1];
            if(toCoverMore-distanceBetweenTwo == distances[node_neigh]){
                res=(res + countWays(node_neigh,adj,distances,toCoverMore-distanceBetweenTwo,dp))%(1000000007);                   
            }                
        } 

        return dp[u] = res;
    }


    int countPaths(int n, vector<vector<int>>& roads) {
      vector<vector<vector<long long>>> adj(n);
      for(vector<int> road:roads){
        long long u = road[0];
        long long v = road[1];
        long long time = road[2];
        adj[u].push_back({v, time});
        adj[v].push_back({u, time});
      }

      vector<long long> distances = dijkstra(n,adj);      
      vector<long long> dp(n, -1);

      long long ways = countWays(0, adj, distances, distances[0], dp);      
      return ways;      

    }
};
