class Solution {
    public int countPaths(int n, int[][] roads) {
        ArrayList<ArrayList<Node>> list = new ArrayList<ArrayList<Node>>();
        for(int i=0;i<n;i++){
            list.add(new ArrayList());
        }
        for(int arr[] :roads){
            list.get(arr[0]).add(new Node(arr[1],arr[2]));
            list.get(arr[1]).add(new Node(arr[0],arr[2]));
        }
        int min = diskshtra(list,n,0);
        return min;  

    }
    public int diskshtra(ArrayList<ArrayList<Node>> adj,int n,int start){
        int mod = 1_000_000_007;
        long dis[]= new long[n];
        long ways[]= new long[n];
        ways[0]=1;
        Arrays.fill(dis,Long.MAX_VALUE);
        PriorityQueue<Node> pq = new PriorityQueue<Node>(n,new Node());
        dis[start]=0;
        pq.add(new Node(start,0));
        while(pq.size()>0){
            Node node = pq.poll();
            if(node.val>dis[node.dis]){
                continue;
            }
            for(Node nextnode:adj.get(node.dis)){
                if(dis[node.dis]+nextnode.val<dis[nextnode.dis]){
                    dis[nextnode.dis]= dis[node.dis]+nextnode.val;
                    ways[nextnode.dis]= ways[node.dis]%mod;
                    pq.add(new Node(nextnode.dis,dis[nextnode.dis]));
                }
                else if(dis[node.dis]+nextnode.val==dis[nextnode.dis]){
                    ways[nextnode.dis]= (ways[node.dis]+ways[nextnode.dis])%mod;
                }

            }
        }
        return (int)ways[n-1];

    }

}

