            int u = pq.top().second;
            if (u==n-1)
                break;
            pq.pop();
            for (pair<int, int> dest : mat[u]) {
                int v = dest.first;
                long long weight = dest.second;
                if (dist[v] == dist[u] + weight) {
                    count[v] = (count[v] + count[u]) % MOD;
                }
                else if (dist[v] > dist[u] + weight) {
                    dist[v] = dist[u] + weight;
                    pq.push(make_pair(dist[v], v));
