        pq.push({0,0}); // distance , node

        while(!pq.empty()){
            ll time = pq.top().first;
            ll node = pq.top().second;
            pq.pop();

            for(auto i:adj[node]){
                ll adjNode = i.first;
                ll weight = i.second;

                if(time+weight<timevec[adjNode]){
                    timevec[adjNode]=time+weight;
                    pq.push({timevec[adjNode],adjNode});
