class Solution {
public:
    int countPaths(int n, vector<vector<int>>& roads) {
        vector<vector<pair<int,double>>> mat(n,vector<pair<int,double>>());
        for(auto&e:roads) mat[e[0]].push_back({e[1],e[2]}), mat[e[1]].push_back({e[0],e[2]});

        priority_queue<pair<double,int>,vector<pair<double,int>>,greater<pair<double,int>>>q;
        q.push( {0, n-1});

        vector<bool>visited(n,false);
        vector<double> dists(n,0);

        while(!q.empty()) {
            auto t=q.top();q.pop();
            if(visited[t.second]) continue;

            visited[t.second]=true;
            dists[t.second]= t.first;

            for(auto& next: mat[t.second])
                if(!visited[next.first])  q.push({t.first+next.second,next.first});
        }

        visited.assign(n,false);
        vector<int>cache(n,-1);
        auto minDist = dists[0];
        return getCount(mat,n,dists,visited,0,minDist,cache);
    }

    int getCount(vector<vector<pair<int,double>>> &mat, int n, vector<double>& dists,vector<bool>&visited, int from,double minDist,vector<int>&cache) {
        if(from==n-1) return 1;

        if(cache[from]>=0) return cache[from];
        visited[from]=true;
        int r =0;
        for(auto& next: mat[from])
            if(!visited[next.first] && dists[next.first]==(minDist-next.second))
              r = (r+ getCount(mat,n,dists,visited,next.first,minDist-next.second,cache))%1000000007;

        visited[from]=false;
        cache[from] = r;
        return r;
    }
};
