public class Solution {
    private int dijkstra(List<int[]>[] graph, int start, int end, long[] distance){
        int MOD = 1000000007;
        int[] ways = new int[end];

        // set distance for source to 0
        distance[start] = 0;
        ways[start] = 1;

        // (src, d)
        var pq = new PriorityQueue<int, long>();
        pq.Enqueue(start, 0);

        while(pq.TryDequeue(out int u, out long u_d)){      

            foreach(var vertex in graph[u]){
                int v = vertex[0]; 
                int v_t = vertex[1];

                if(u_d > distance[v]){
                    continue;
                }

                // update if distance is minimum
                if (distance[v] > u_d + v_t){
                    distance[v] = u_d + v_t;
                    ways[v] = ways[u];

                    pq.Enqueue(v, distance[v]);
                }
                else if(distance[v] == u_d + v_t){
                    ways[v] = (ways[v] + ways[u]) % MOD;
                }
            }            
        }
        return ways[end - 1];
    }

    public int CountPaths(int n, int[][] roads) {
        int start = 0;
        int end = n;

        var graph = new List<int[]>[n];
        for(int i = 0; i < n; i++){
            graph[i] = new List<int[]>();
        }

        var distance = new long[n];
        // mark all distances to infinity.
        Array.Fill(distance, long.MaxValue);

        // adjacency list for un-directed graph
        foreach(var r in roads){
            int u = r[0];
            int v = r[1];
            int t = r[2];
            graph[u].Add(new int[]{ v, t });
            graph[v].Add(new int[]{ u, t });
        }

        return dijkstra(graph, start, end, distance);
    }
}
