class Solution {
public:
    int countPaths(int n, vector<vector<int>>& roads) {

        vector<pair<int , int>> adj[n] ; 
        long long modu = 1000000007  ; 

        for (auto &it: roads) {
            adj[it[0]].push_back({it[1] , it[2]}) ; 
            adj[it[1]].push_back({it[0] , it[2]}) ; 
        }

        vector<long long> distance(n, LONG_MAX) ; 
        vector<long long> PossiblePaths(n) ; 

        int source =  0; 

        distance[source] = 0 ;

        PossiblePaths[0] = 1 ;  

        // Priority queue to always get minimum distance vertex at top
        priority_queue < pair<long long , long long> , vector<pair<long long , long long>> , greater<> > Q ; 

        Q.push({distance[source] , source}) ; 

       while (!Q.empty()) {

           auto [ u_cost , u ] = Q.top() ; 
           Q.pop() ; 

           for (auto &adjacent_vertice: adj[u]) {

               auto [ v , v_cost ] = adjacent_vertice ;

               // Relaxation 
               if (distance[u] + v_cost < distance[v]) 
               {
                   distance[v] = distance[u] + v_cost ; 
                   Q.push ( { distance[v] , v } ) ;  

                   PossiblePaths[v] = PossiblePaths[u] ;
               }
               else if ( distance[v] == u_cost + v_cost ) 
               {
                   PossiblePaths[v] += PossiblePaths[u] ;
                   PossiblePaths[v] = PossiblePaths[v] % modu ; 
               }
           }

       }

        return PossiblePaths[n-1] ;

    }
};
