class Solution:
    def countPaths(self, n: int, roads: List[List[int]]) -> int:
        graph = defaultdict(list)
        times = []
        shortest_time = float('inf')

        for s, d, t in roads:
            graph[s].append([d, t])
            graph[d].append([s, t])

        to_explore = [[0, [0], 0]]

        while to_explore:
            v, path, time = to_explore.pop()
            if v == n-1:
                times.append(time)
                shortest_time = min(shortest_time, time)
            else:
                to_explore.extend([[i[0], path+[i[0]], time+i[1]] for i in graph[v] if i[0] not in path and time+i[1] <= shortest_time])
        return Counter(times)[min(times)]

