class Solution:
    def countPaths(self, n, roads):
        """
        Time: O(R + NlogN) where R is number of roads and N is number of nodes
        Space: O(R + N) where R is number of roads and N is number of nodes
        """
        g = defaultdict(lambda: {})
        for u, v, w in roads:
            g[u][v] = g[v][u] = w

        minq, min_dist, paths = [(0, 0)], defaultdict(lambda: math.inf), [int(i == 0) for i in range(n)]
        while minq[0][1] != n - 1:
            dist, u = heapq.heappop(minq)
            if dist > min_dist[u]:
                continue
            for v, w in g[u].items():
                new_dist = dist + w
                if new_dist == min_dist[v]:
                    paths[v] = (paths[v] + paths[u]) % 1000000007
                elif new_dist < min_dist[v]:
                    heappush(minq, (new_dist, v))
                    paths[v], min_dist[v] = paths[u], new_dist
        return paths[n - 1]
