class Solution:
    def countPaths(self, n: int, roads: List[List[int]]) -> int:
        heap = [(0, 0, 0)]
        roadsDict = defaultdict(list)
        visited = defaultdict(int)
        numWaysDict = defaultdict(int)
        numWaysDict[0] = 0

        for r1, r2, dist in roads:
            roadsDict[r1].append((r2, dist))
            roadsDict[r2].append((r1, dist))

        while heap:
            dist, road, prevRoad = heappop(heap)
            if road in visited and dist > visited[road]:
                continue

            numWaysDict[road] += max(1, numWaysDict[prevRoad])
            if road in visited:
                continue

            visited[road] = dist

            for nextRoad, travelDist in roadsDict[road]:
                heappush(heap, (dist + travelDist, nextRoad, road))

        return numWaysDict[n-1] % (10**9 + 7)
