        priority_queue<pair<long long,int>,vector<pair<long long,int>>,greater<pair<long long,int>>> q;
        q.push({0,0}); //start node, initial distance
        distance[0]=0;
        vector<long long>ways(n,1);
        ways[0]=1;
        /*
        this here means, we will calculate number of ways to reach each node if that nodes' distance is updating,
        initially number od ways to reach first node i.e. 0 is always 1, now to reach 4 and 1 there is 1 ways as in intial
        run their distance will be updated to less distance hence it's no. of way will be no. way of reaching to it's
        parent, and if any distance is same for a node, that means it is reachable via ways++.
        */
        while(!q.empty()){
            int parent = q.top().second;
            int parentwei = q.top().first;
            q.pop();
            if(distance[parent]<parentwei) continue; //dont;take this node if shortest distance for this node has already
            // been encountered
            for(int i=0;i<graph[parent].size();i++){
                int child = graph[parent][i].first;
                long long childweight = graph[parent][i].second;
                if(distance[parent]+childweight < distance[child]){ // 0+5(in case of node 4) < INT_MAX
                    distance[child]=distance[parent]+childweight;
