public class Solution {
    public int CountPaths(int n, int[][] roads) {

        int MOD = 1000000007;
        var graph = new List<int[]>[n];
        for(int i = 0; i < n; i++)
            graph[i] = new List<int[]>();

        foreach(var road in roads)
        {            
            graph[road[0]].Add(new int[] {road[1], road[2]});
            graph[road[1]].Add(new int[] {road[0], road[2]});
        }       

        var pq = new SortedSet<(long time, int src)>();
        pq.Add((0, 0));

        var time = new long[n]; //gotcha long time
        var ways = new int[n];
        for(int i = 0;  i < n; i++)
            time[i] = long.MaxValue;
        ways[0] = 1;
        time[0] = 0;

        while(pq.Count > 0)
        {
            var item = pq.Min;
            pq.Remove(pq.Min);
            var u = item.src;
            var time_u = item.time;

            if(time_u > time[u])
                continue;

            foreach(var node in graph[u])
            {
                var v = node[0];
                var time_uv = node[1];
                if(time[v] > time[u] + time_uv)
                {
                    time[v] = time[u] + time_uv;
                    ways[v] = ways[u];
                    pq.Add((time[v], v));
                }
                else if (time[v] == time[u] + time_uv)
                {                    
                    ways[v] = (ways[v] + ways[u])%MOD;
                }
            }            
        }
        return ways[n - 1];       
    }
}
