    int countPaths(int n, vector<vector<int>>& roads) {
        vector<vector<pair<long long, long long>>> graph(n);
        for(auto &x : roads){
            graph[x[0]].push_back({x[1],x[2]});
            graph[x[1]].push_back({x[0],x[2]});
        }

        vector<long long> distance(n,INF);
        distance[0]=0;

        priority_queue<pair<long long,long long>, vector<pair<long long,long long>>, cmp> q;
        q.push({0,0});

        vector<long long> dp(n,0);
        dp[0]=1;

        while(!q.empty()){
            long long curNode = q.top().first;
            long long curDis = q.top().second;
            q.pop();

            for(auto &child : graph[curNode]){
                if(curDis+child.second < distance[child.first]){
                    distance[child.first] = curDis+child.second;
                    q.push({child.first, distance[child.first]});
                    dp[child.first] = dp[curNode] % MOD;
                }else if(curDis+child.second == distance[child.first]){
                    dp[child.first] = (dp[child.first]+dp[curNode]) % MOD;
                }
            }
        }
        return dp[n-1];
