#define ll long long int
#define F first
#define S second
const int M=1e9+7;
class Solution {
public:
      //cost[i] -> shortest time to reach destination from node i
      //dp[i] -> no of ways to reach destination from node i in shortest time
    ll cal(ll node,ll par,vector<ll> &dp,vector<ll> &cost,vector<vector<pair<ll,ll>>> &g,ll n){
        //If reached node "n-1" return 1
        if(node==n-1)
            return 1;
        if(dp[node]!=-1)
            return dp[node];
        ll ans=0;
        for(auto &nbr:g[node]){
            if(nbr.F == par)
                continue;
            //Calculate ans for only those nodes which are part of shortest path from "node"-> n-1 i.e shortest time to reach dest. from "node" == edge weight + shortest time to reach dest. from child node 
            if(cost[node] == nbr.S + cost[nbr.F]){
                ans+=cal(nbr.F,node,dp,cost,g,n);
                ans%=M;
            }
        }
        return dp[node]=ans;
    }
    int countPaths(int n, vector<vector<int>>& roads) {

        //Instead of running dijkstra from source node , run it from destination node i.e n-1 and calculate shortest time to reach each and every node i from destination node
        vector<vector<pair<ll,ll>>> g(n+1);
        for(auto &i:roads){
            g[i[0]].push_back({i[1],i[2]});
            g[i[1]].push_back({i[0],i[2]});
        }
        priority_queue<vector<ll>,vector<vector<ll>>,greater<vector<ll>>> q;
        vector<ll> cost(n,-1),dp(n,-1),vis(n,0);

      //Dijkstra 

        q.push({0,n-1});
        while(!q.empty()){
            auto curr=q.top();
            q.pop();
            ll node=curr[1];
            ll time=curr[0];

            if(vis[node])
                continue;
            cost[node]=time;
            vis[node]=1;
            for(auto &nbr:g[node]){
                if(!vis[nbr.F]){
                    q.push({nbr.S+ time ,nbr.F});
                }
            }
        }
        //If no path exists from target node to starting node return 0
        if(cost[0]==-1)
            return 0;
        //Calculate no of ways to reach destination from source node in shortest time
        return cal(0,-1,dp,cost,g,n);


    }
};
