/**
 * @param {number} n
 * @param {number[][]} roads
 * @return {number}
 */
var countPaths = function(n, roads) {
    const MOD = 10 ** 9 + 7;
    const graph = buildGraph(roads, n);
    const times = new Array(n).fill(Infinity);
    const ways = new Array(n).fill(0);

    times[0] = 0;
    ways[0] = 1;
    const priorityList = [[0, 0]]

    while (priorityList.length) {
        let [node, t] = priorityList.pop();

        for (let [neighbor, weight] of graph[node]) {
            const newTime = t + weight;

            if (newTime < times[neighbor]) {
                times[neighbor] = newTime;
                priorityList.push([neighbor, newTime]);
                priorityList.sort((a, b) => b[1] - a[1]);
                ways[neighbor] = ways[node];
            } else if (newTime === times[neighbor]) {
                ways[neighbor] += ways[node] % MOD;
            }
        }
    }

    return ways[n-1] % MOD;
};

var buildGraph = function (edges, n) {
    const graph = Array.from({length: n}, () => Array());

    for (let [n1, n2, w] of edges) {
        graph[n1].push([n2, w]);
        graph[n2].push([n1, w]);
    }

    return graph;
}
```

