class Solution {
public:
    int countPaths(int n, vector<vector<int>>& roads) {
        vector<vector<pair<int,long long int>>>adj(n+1);
        for(int i = 0;i<roads.size();i++){
            int u =roads[i][0], v = roads[i][1];
            long long int w = roads[i][2];
            adj[u].push_back({v,w});
            adj[v].push_back({u,w});

        }
        const int MOD = 1e9 + 7;

        priority_queue<pair<long long int,int>,vector<pair<long long int,int>>,greater<pair<long long int,int>>>pq;
        vector<long long int>dist(n+1,1e9+7);
        vector<int>ways(n+1,0);
        pq.push({0,0});
        ways[0]=1;
        dist[0] = 0;
        while(!pq.empty()){
            auto it = pq.top();
            pq.pop();
            long long int dis = it.first;
            int node = it.second;
            for(auto x : adj[node]){
                int adjNode = x.first;
                long long int edgW = x.second;
                if(dist[adjNode] > dis + edgW or (dist[adjNode]==(1e9+7)) ){
                    ways[adjNode] = ways[node];
                    ways[adjNode] %= MOD;
                    dist[adjNode] = dis + edgW;
                    pq.push({dist[adjNode],adjNode});
                }else if(dist[adjNode] == dis + edgW){
                    ways[adjNode] += ways[node];
                    ways[adjNode] %= MOD;
                }
            }
        }
        return ways[n-1];

    }
};
