class Solution {
public:
    int countPaths(int n, vector<vector<int>>& roads) 
    {
        int mod = 1e9 + 7;
        set<pair<long long , long long>> store;
        vector<vector<pair<long long, long long>>>adj(n);
        vector<long long> ways(n , 0);
        vector<long long> dist(n , LONG_MAX);
        for(auto edge : roads)
        {
            adj[edge[0]].push_back({edge[1],edge[2]});
            adj[edge[1]].push_back({edge[0],edge[2]});
        }
        store.insert({0 , 0});
        ways[0] = 1;
        dist[0] = 0;
        while(!store.empty())
        {
            auto begin = *(store.begin());
            int node = begin.second;
            long long nodeDist = begin.first;
            store.erase(begin);
            for(auto edge : adj[node])
            {
                int adjNode = edge.first;
                long long wt = edge.second;
                long long newDist = nodeDist + wt ;
                if(dist[adjNode] > newDist)
                {
                    store.insert({newDist , edge.first});
                    ways[adjNode] = ways[node];
                    dist[adjNode] = newDist;
                }
                else if(dist[edge.first] == newDist)
                {
                    ways[adjNode] = (ways[adjNode] + ways[node])%mod;
                }
            }
        }
        return ways[n-1];
    }
};
