class Solution {
    public int countPaths(int n, int[][] roads) {
     // Creating an adjacency list for the given graph.
        ArrayList < ArrayList < Pair >> adj = new ArrayList < > ();
        for (int i = 0; i < n; i++) {
            adj.add(new ArrayList < > ());
        }
        int m = roads.length;
        for (int i = 0; i < m; i++) {
            adj.get(roads[i][0]).add(new Pair(roads[i][1],(long) roads[i][2]));
            adj.get(roads[i][1]).add(new Pair(roads[i][0],(long) roads[i][2]));
        }

        // Defining a priority queue (min heap). 
        PriorityQueue < Pair > pq = new PriorityQueue < Pair > ((x, y) -> Long.compare(x.second ,y.second));

        // Initializing the dist array and the ways array
        // along with their first indices.

        int[] ways = new int[n];
        long[] dist= new long[n];
        Arrays.fill(dist, Long.MAX_VALUE/2);
        dist[0] = 0;
        ways[0] = 1;
        pq.add(new Pair(0, 0));

        // Define modulo value
        int mod = (int)(1e9 + 7);  

        while(pq.size()!=0){

            int node = pq.peek().first;
             long weight= pq.peek().second; // time take to reach current node { min in heap}
             pq.remove();

            for(Pair e : adj.get(node)){

                int edjnode = e.first; //adjecent node
                long edjWeight= e.second;


                if(weight + edjWeight < dist[edjnode]){ // if recorded weight is greater than current than change it , as we found new sortest distance to reach that adjecent node
                // this also tell us its first time with this distance so num of ways count is same as its parent node has
                //as it is possible that parent has more than 1 way to reach it , to its will indirectly become numbers of ways to reach this adjcent node
                    dist[edjnode] = weight + edjWeight;  
                    //add it to min. heap for future calls
                    pq.add(new Pair(edjnode,weight + edjWeight ));
                    ways[edjnode]= ways[node];

                }



                //now if its note a first time  with new shortest distanc e
                // we got new conditions , either new calculated weight becoms larger than previouslly recoded or equal
                //if its larger we don't consider that path okay

                //what if its equal 
                    //means we reach again with same weight to same node 
                    //so if its note first than we can say that 
                    //it is a another way to reach this current adjecent node
                    //via a defferent parent node 
                    //so add it to the ways of reach this adjecent node

                  else if(weight + edjWeight == dist[edjnode]){

                        ways[edjnode]= ( ways[edjnode] + ways[node]) % mod;
                  }

            }

        }
   // Finally, we return the no. of ways to reach
        // (n-1)th node modulo 10^9+7.
        return ways[n-1] % mod;
    }
}

class Pair {
    int first;
    long second;
    public Pair(int first, long second) {
        this.first = first;
        this.second = second;
    }
}

```

