class Solution {
public:
    int countPaths(int n, vector<vector<int>>& roads) {
        // shortest path:: dijkastra:: min_heap_priority queue:: cnt all ways::
        // adjacency list of (u->v, wts)
        vector<vector<pair<int,int>>>adj(n);
        for(int i=0;i<roads.size();i++){
          int u = roads[i][0];
          int v = roads[i][1];
          int wt = roads[i][2];
          adj[u].push_back({v, wt});
          adj[v].push_back({u, wt});
        }
        // min heap priority queue.
        priority_queue< pair<long , long>, vector<pair<long , long>>, greater<pair<long , long>>  > pq;
        pq.push({0, 0}); // starting distance and node 
        vector<long >distance(n, 1e18); 
        vector<int>ways(n,0);
        ways[0] = 1;
        int mod = 1e9+7;
        distance[0] = 0;
        int cnt = 0;
        // int shortest_path = 1e9+1;
        while(!pq.empty()){
            auto it = pq.top();
            pq.pop();
            int node = it.second;
            long  dist = it.first;
            // if we visited the target: keep getting the shortest distance guy.
            if(node == n-1){
               return ways[node]%mod;
                // shortest_path = dist;

            }

            // visiting all the adjacent neighbours of node.
            for(auto itr: adj[node]){
                long  wt = itr.second;
                int target = itr.first;
                // if we got better path to reach target.
                if(distance[target] == wt+dist){
                    ways[target] = (ways[target]%mod + ways[node]%mod)%mod;
                }
                else if(distance[target] > wt+dist) {
                    ways[target] = ways[node];
                    distance[target] = wt+dist;
                    pq.push({wt+dist, target});

                }

            }
        }
            return -1;

    }
};
