class Solution {
public:
    int countPaths(int n, vector<vector<int>>& roads) {
        vector<long long>dist(n,LLONG_MAX);
        vector<pair<int,int>>adj[n];
        vector<int>ways(n,0);
        for(int i = 0;i<roads.size();i++){
            long long u = roads[i][0];
            long long v = roads[i][1];
            long long cost = roads[i][2];
            adj[u].push_back({v,cost});
            adj[v].push_back({u,cost});
        }
        dist[0] = 0;
        ways[0] = 1;
        int mod = (int)1e9+7;
        priority_queue<pair<long long,int>,vector<pair<long long,int>>,greater<pair<long long,int>>>pq;
        pq.push({0,0});
        while(!pq.empty()){
            pair<long long,int> temp = pq.top();
            long long distance = temp.first;
            long long node = temp.second;
            pq.pop();
            for(auto i:adj[node]){
                long long adjnode = i.first;
                long long cost = i.second;
                if(cost + distance < dist[adjnode]){
                    dist[adjnode] = (cost + distance)%mod;
                    pq.push({dist[adjnode],adjnode});
                    ways[adjnode] = (ways[node])%mod;
                }
                else if(cost + distance == dist[adjnode]){
                        ways[adjnode] = (ways[adjnode] + ways[node])%mod;
                }
            }
        }
        return ways[n-1];
    }
};
