class Solution {
public:
    int numFriendRequests(vector<int>& ages) {
        int map[121] = { 0 };
        int minage = 0, ans = 0, group = 0;

        for (auto a : ages)
            map[a]++;

        for (int i = 1; i < 121; ++i)
            map[i] += map[i-1];

        for (int i = 15; i < 121; ++i) {
            minage = (i/2 + 7);
            group = map[i]-map[i-1];
            ans += group * (map[i] - map[minage] - 1); 
        }
        return ans;
    }
};
