    public static int findIndex(int[] ages, int curr_age, int target) {
        if (curr_age >= 100) {
            target = Math.max(target, 100);
        }

        int left = 0;
        int right = ages.length - 1;

        while (left <= right) {
            int mid = left + (right - left) / 2;
            if (ages[mid] >= target) {
                right = mid - 1;
            } else {
                left = mid + 1;
            }
        }
