class Solution {
    public int numFriendRequests(int[] ages) {
         Map<Integer,Integer> mp = new HashMap<>();

         for(int age : ages){
            mp.put(age,mp.getOrDefault(age,0) + 1);
         }
        int count = 0; //count of friend requests
        for(int a : mp.keySet()){
           for(int b : mp.keySet()){
              if(canSendFReq(a,b) == true){
                  int AsF = mp.get(a); //A's Friends
                  int BsF = mp.get(b); //B's Friends
                //As a person cannot send req to himself
                  if(a == b){
                     BsF--;   //Reduce B's friends by one as B is iterating inside the inner loop
                  }
			   //As both of them gonna sent req to each other (also persons with same ages will be doing same thing)
                 count = count + AsF * BsF;
              }
           }
        }
      return count;
    }
    public boolean canSendFReq(int x,int y){
        return !(y <= 0.5 * x + 7 || y > x || (y > 100 && x < 100));
    }
}
