	public int numFriendRequests(int[] ages) {
        Arrays.sort(ages);
        int num=0;
        for(int i=0;i<ages.length;i++) {
            int left = leftBound(ages[i]/2+8,ages), right = rightBound(ages[i], ages);
            num+=Math.max(right-left,0);   
