```Python3 []
class FriendRequest:
    def _binarySearch(self, ages, target):
        lo = 0
        hi = len(ages) - 1
        while lo <= hi:
            mid = lo + (hi - lo) // 2
            if ages[mid] <= target:
                lo = mid + 1
            else:
                hi = mid - 1
        return lo

    def numOfRequests(self, ages):
        requests = 0
        ages.sort()
        for age in ages:
            left = self._binarySearch(ages, age // 2 + 7)
            right = self._binarySearch(ages, age) - 1
            requests += max(0, right - left)
        return requests

class FriendRequest2:
    def _bucketSort(self, ages):
        buckets = [0] * 121
        for age in ages:
            buckets[age] += 1
        return buckets

    def _buildPrefixSum(self, buckets):
        prefix = [0] * 121
        for i in range(1, 121):
            prefix[i] += prefix[i - 1] + buckets[i]
        return prefix

    def numOfRequests(self, ages):
        buckets = self._bucketSort(ages)
        prefix = self._buildPrefixSum(buckets)
        requests = 0
        for age in range(15, 121):
            if buckets[age] == 0:
                continue
            cur_requests = prefix[age] - prefix[age // 2 + 7]
            requests += buckets[age] * cur_requests - buckets[age]
        return requests

class Solution:
    def numFriendRequests(self, ages: List[int]) -> int:
        return FriendRequest2().numOfRequests(ages)
