class Solution {
    public int numFriendRequests(int[] ages) {
        ages = ages == null ? new int[0] : ages;

        int[] count = new int[121];
        int requests = 0;

        for (int age : ages)
            count[age]++;

        for (int a = 1; a <= 120; a++) {
            for (int b = 1; b <= 120; b++) {
                if (count[a] + count[b] <= 1 || aCannotRequestB(a, b))
                    continue; //not enough people or any of the rule violates

                int numAgesOfA = count[a], numAgesOfB = count[b];
                if (a == b) //special case if both a and b are pointing to the same age slot
                    numAgesOfB--;

                requests += numAgesOfA * numAgesOfB;
            }
        }

        return requests;
    }

    private boolean aCannotRequestB(int a, int b) {
        return b <= (a / 2 + 7) || b > a || b > 100 && a < 100;
    }
}
