int numFriendRequests(int* ages, int agesSize){
int count=0;
int i, j;
int min_age;
int max_age;
int age_hash[150];
// Initialize a hash table of ages, 0 to 149, and hash all ages into it
for (i=0; i<150; i++) age_hash[i]=0;
for (i=0; i<agesSize; i++) age_hash[ages[i]]++;

// Reduce the work by finding youngest and oldest ages
for (i=0; i<150; i++) if (age_hash[i]!=0) { min_age=i; break; }
for (i=149; i>0; i--) if (age_hash[i]!=0) { max_age=i; break; }

// Check friending status of each pair of ages, and multiply by how
// many of each we have; if ages are same, remember you can't friend
// yourself!
for (i=min_age; i<=max_age; i++)
    for (j=min_age; j<=max_age; j++) {
        if ((age_hash[i]!=0) && (age_hash[j]!=0)) {
          if ((j>0.5*i+7) && (j<=i) && 
              ((j<=100) || (i>=100)))
            if (i==j) count += age_hash[i] * (age_hash[i]-1);
              else count += age_hash[i] * age_hash[j];
        }
    }

return count;
}

