int
numFriendRequests(const int *const ages, const int agesSize)
{
	static int cnt[121];
	int a, b, sum;

	(void)memset(cnt, 0, sizeof(cnt));
	for (a = 0; a < agesSize; ++a)
		++cnt[ages[a]];
	sum = 0;
	for (a = 15; a <= 120; ++a)
		if (cnt[a] != 0)
			for (b = a / 2 + 8; b <= a; ++b)
				if (cnt[b] != 0)
					sum += (cnt[a] - (a == b)) * cnt[b];

	return sum;
}
