class Solution {
public:
    int numFriendRequests(vector<int>& ages) {
        sort(ages.begin(), ages.end());
        int ans = 0;
        for(int i = 0; i < ages.size(); i++){
            int j = i+1;
            while(j<ages.size() && ages[i]==ages[j]) j++;
            j--;
            auto itr = upper_bound(ages.begin(), ages.end(), 0.5*ages[i] + 7);
            if(itr != ages.end() && (itr-ages.begin())<j) {
                ans += j - (itr-ages.begin());
            }
        }
        return ans;
    }
};
