func numFriendRequests(ages []int) int {
    people := make([]int, 121)

    for _, age := range ages {
        people[age]++
    }

    res := 0

    for i := 1; i <= 120; i++ {
        if people[i] == 0 { continue }

        limit := (i / 2) + 7

        for j := limit + 1; j < i; j++ {
            res += people[i] * people[j]
        }

        if i > limit {
            res += people[i] * (people[i] - 1)
        }
    }

    return res
}
