class Solution:
    def numFriendRequests(self, ages: List[int]) -> int:
        counter = Counter(ages)
        s = sorted(counter.keys())
        prefix = []
        sum_total = 0
        for i in s:
            sum_total += counter[i]
            prefix.append(sum_total)


        j = 0
        count = 0
        for i, age in enumerate(s):
            while j < i and (s[j] <= 0.5*age + 7):
                j += 1
            if j == i and s[j] <= 0.5*age + 7:
                continue
            if counter[s[i]] > 1:
                count += (counter[s[i]] * (counter[s[i]]-1))
            count += (counter[s[i]] * ((prefix[i-1] if i > 0 else 0) - (prefix[j-1] if j > 0 else 0)))
        return count

