```
function numFriendRequests(ages: number[]): number {

    let requests = 0
    ages.sort((a, b) => b - a)

    for (let i = 0; i < ages.length - 1; i++) {
        const mylimit = Math.floor(ages[i] / 2) + 7
        inner: for (let k = i + 1; k < ages.length; k++) {
            if (ages[k] > mylimit) {
                requests++
                if (ages[k] === ages[i]) {
                    requests++
                }
            } else {
                break inner

            }
        }
    }
    return requests
};
