    var length = ages.count
    for i in 0..<length{
        var leftMostValidIndex = -1
        var start1 = 0
        var end1 = length-1
        while start1 <= end1{
            var mid = start1 + (end1 - start1)/2
            if Double(ages[mid]) > Double(0.5) * Double(ages[i]) + 7{
