int helper(vector<int>&ages,int target)
{
    int low=0;
    int high=ages.size()-1;
    while(low<=high)
    {
        int mid=low+(high-low)/2;
        if (ages[mid]<=target)
        low=mid+1;
        else high=mid-1;
