    }

    int numFriendRequests(vector<int>& ages) {
        sort(ages.begin(), ages.end(), greater<int>()); // Non-increasingly sort

        int ans=0;
        for(int i=0; i<ages.size(); i++){
            double tgt= 1.0*ages[i];  // target

            /*
                1. (arr[y] > arr[x]/2 +7) &&
                2. (arr[y] <= arr[x]) &&
                3. (arr[x]>=100 || arr[y]<=100)
            */

            int idx1= binarySearch1(ages, i, ages.size()-1, 0.5*tgt+7); // cond 1 : arr[y] > arr[x]/2 +7
            // valid positions are [i-idx1]
            if(idx1==-1){ continue; }

            int idx2= ages.size()-1; // cond 2 : arr[y] <= arr[x]
            // valid positions are [i-idx2]

            int idx3;
            if(ages[i]>=100){ idx3= i; } // all index of y are valid
            else{
                idx3= binarySearch3(ages, i, ages.size()-1, 100); // cond 3: arr[y] <=100
                if(idx3==-1){ continue; }
            }
            // valid positions are [idx3-ages.size()-1]

            // cout<<idx1<<" "<<idx2<<" "<<idx3<<endl;

            // Now we need intersection of all 3 conditions.
            int finalIdxHigh= min(idx1, idx2);
            int finalIdxLow= max(i, idx3);

            ans+=(finalIdxHigh-finalIdxLow);
            if(finalIdxLow>i){ ans++; }   
        }

        /*
            👉 Now here is a catch : assume [16,16] (repeating case).
            👉 Now when we are at i(here x), we are taking care of all index in [i,size-1]. So, when we are at 0th idx, we are taking care of indexes from [0,1], 
            and we get at pair ( 0-->1 ), but in this case ( 1-->0 ) is also a valid pair which we are missing.
            👉 For this reason we are storing freq of each element and handling repeating case separately below.

            👉 For eg: assume [16,16,16]
            👉 Above process will handle cases like (0->1), (0->2), (1->2); so ans=3. But we are missing cases like (1->0), (2->1), (2->0). 
            👉 From hashmap, we know m[16]=3 (freq). So we just add ans+=((3-1)*3)/2;
        */

        unordered_map<int, int> m; // {element, freq}
        for(auto it: ages){  m[it]++;  }

        for(auto it: m){
            int num= it.first; int n= it.second;
            bool flag= 1.0*num > 0.5*num +7;
            if(n>1 && flag==true){
                ans+=((n-1)*n)/2; 
            }
        }

        return ans;
    }
};
