impl Solution {
    pub fn num_friend_requests(ages: Vec<i32>) -> i32 {
        let mut count = vec![0; 121];
        for age in ages {
            count[age as usize] += 1;
        }
        let mut result = 0;
        for age in 1..=120 {
            if count[age] == 0 {
                continue;
            }
            for other in (age / 2 + 8)..=age {
                if count[other] == 0 {
                    continue;
                }
                if age == other {
                    result += count[age] * (count[age] - 1);
                } else {
                    result += count[age] * count[other];
                }
            }
        }
        result
    }
}
