class Solution:
    def numFriendRequests(self, ages: List[int]) -> int:

        age_count = [0] * 121
        summ = 0
        dic = {}

        # count ages frpom 0 to 120
        for age in ages:
            age_count[age] += 1

        # range sum, helps in calculating number of friend requests to be sent 
        # without binary search
        for i in range (121):
            summ += age_count[i]
            dic[i] = summ


        total_count = 0
        # loop through all the ages
        # Alternately, You can even loop through 0 to 120 and still be able to calculate
        # that will reduce time complexity to O(120) basically almost constant time for inputs of any sizes

        for age in ages:
            lower_age = (age * 0.5) + 7
            rem = lower_age % 1.0
            lower_age = int(lower_age-0.5) if rem>0 else int(lower_age)
            higher_age = int(min(age, 100) if age < 100 else age)

            if lower_age>higher_age:
                continue

            if lower_age<age<=higher_age:
                decrease_count=1
            else:
                decrease_count = 0
            if higher_age==lower_age:
                increase_count = 0
            else:
                increase_count = dic[higher_age] - dic[lower_age]

            total_count += increase_count - decrease_count

        return total_count


