var numFriendRequests = function(ages) {
	const agesMap = new Map();
	const agesSum = Array(121).fill(0);

	ages.forEach(age => {
		const count = agesMap.get(age) ?? 0;
		agesMap.set(age, count + 1);
	});

	for (let age = 1; age < 121; age++) {
		agesSum[age] = (agesMap.get(age) ?? 0) + agesSum[age - 1];
	}

	return [...new Set(ages)].reduce((result, age) => {
		const validAge = (age * 0.5 | 0) + 7;
		if (validAge >= age) return result;
		const multiple = agesSum[age] - agesSum[validAge];
		return result + (multiple - 1) * agesMap.get(age);
	}, 0);
};

