class Solution {
    public int numFriendRequests(int[] ages) { 
        int[] numPerAge = new int[150];

        for(int i : ages)
            numPerAge[i] += 1;

        int sum = 0;
        for(int currentAge = 0; currentAge <150; currentAge++) {
            //if perf
            if(numPerAge[currentAge] == 0)
                continue;

            // To be elibigle to receive request
            // Y person should be greater than or equal to 'startAge'. (+1 is for equal and greater than)
            int startAge = (int)(currentAge * 0.5) + 7 + 1;

            // If Eligible age is greater than current age, 
            // Person of currentAge wont be able to send any request due to rule#2.
            if(startAge > currentAge)
                continue;

            // Get count of all person whoes age starts with startAge and currentAge-1;
            int eligiblePeopleCount = getCount(numPerAge, startAge, currentAge-1);

            // Persons of Same age (as currentAge) will send request to each of the eligible people.
            sum += (eligiblePeopleCount * numPerAge[currentAge]);

            // X Person can send request to the other presons who is of same age of X.
            sum += (numPerAge[currentAge] * (numPerAge[currentAge] -1));
        }
        return sum;
    }
    private int getCount(int[] numPerAge, int startAge, int currentAge) {
        if(currentAge < startAge)
            return 0;

        int sum = 0;
        for(int i = startAge; i <= currentAge; i++) {
            sum += numPerAge[i];
        }
        return sum;
    }
}
